"""
http://adventofcode.com/2016/day/2
"""

class Day2(object):
    def solutionPart1(input):
        keypad = [
                [1, 2, 3],
                [4, 5, 6],
                [7, 8, 9]]
        x = 1
        y = 1
        code = 0
        for line in input:
            for letter in line:
                if letter == "R":
                    if x + 1 < 3:
                        x += 1
                elif letter == "L":
                    if x - 1 >= 0:
                        x -= 1
                elif letter == "U":
                    if y - 1 >= 0:
                        y -= 1
                elif letter == "D":
                    if y + 1 < 3:
                        y += 1
            code = (code * 10) + keypad[y][x]
        return code

    def solutionPart2(instructions):
        keypad = [
                ["N", "N", "1", "N", "N"],
                ["N", "2", "3", "4", "N"],
                ["5", "6", "7", "8", "9"],
                ["N", "A", "B", "C", "N"],
                ["N", "N", "D", "N", "N"]]
        x = 2
        y = 2
        output = ""
        for line in instructions:
            for letter in line:
                if letter == "R":
                    if x + 1 < 5 and keypad[y][x + 1] != "N":
                        x += 1
                elif letter == "L":
                    if x - 1 >= 0 and keypad[y][x - 1] != "N":
                        x -= 1
                elif letter == "U":
                    if y - 1 >= 0 and keypad[y - 1][x] != "N":
                        y -= 1
                elif letter == "D":
                    if y + 1 < 5 and keypad[y + 1][x] != "N":
                        y += 1
            output += keypad[y][x]
        return output

    if __name__ == '__main__':
        secretNote = ["", "", "", "", ""]
        print(solutionPart1(secretNote))
        print(solutionPart2(secretNote))
